mapper.select:
SELECT COUNT(*) as idCount,
ifnull(emp_seq,0) as emp_seq , id , pw , token FROM emp_tb WHERE id = #{empId} AND pw = #{empPw};
ifnull(custmr_seq,0) as custmr_seq , custmr_id , custmr_pw , custmr_token FROM custmr_tb WHERE custmr_id = #{empId} AND custmr_pw = #{empPw};

mapper.insert:

		SELECT IFNULL(MAX(custmr_seq),0)+1
		FROM custmr_tb;

    INSERT INTO custmr_tb(
    			custmr_seq
    		 ,  custmr_id
    		 ,	custmr_pw
    		 ,	custmr_nme
    		 ,	custmr_mobl
    		 ,  custmr_token
    		 ,  custmr_postcd
    		 ,  custmr_adrs
    		 ,  custmr_detal_adrs
    		 ,	custmr_card_check
    		 ,	custmr_quscnc_check
    		 ,  custmr_gendr
    		 ,  custmr_birth
    		 ,  custmr_eml
    		 )
    VALUES(#{custmrSeq}
    	,  #{custmrId}
    	,  #{custmrPw}
    	,  #{custmrNme}
    	,  #{custmrMobl}
    	,  #{custmrToken}
    	,  #{custmrPostcd}
    	,  #{custmrAdrs}
    	,  #{custmrDetalAdrs}
    	,  #{custmrCardCheck}
    	,  'N'
    	,  #{custmrGendr}
    	,  #{custmrBirth}
    	,  #{custmrEml}
    );

mapper.select:
    SELECT custmr_seq 
    FROM custmr_tb
	WHERE custmr_id = #{custmrId}
	AND custmr_pw = #{custmrPw};

mapper.insert:

	SELECT IFNULL(MAX(shipng_plc_seq),0)+1
	FROM shipping_place_tb;

INSERT INTO 
shipping_place_tb(shipng_plc_seq
				, custmr_seq
				, shipng_plc_nme
				, recvr_nme
				, recvr_mobl
				, shipng_plc_postcod
				, shipng_plc_adrs
				, shipng_plc_detal_adrs
				, exist_check
				)
VALUES(#{shipngPlcSeq}
	,  #{custmrSeq}
	,  #{custmrNme}
	,  #{custmrNme}
	,  #{custmrMobl}
	,  #{custmrPostcd}
	,  #{custmrAdrs}
	,  #{custmrDetalAdrs}
	,  'Y');

mapper.select:
SELECT custmr_id 
FROM custmr_tb
WHERE custmr_nme = #{custmrNme}
AND custmr_mobl = #{custmrMobl};

SELECT custmr_seq
FROM custmr_tb
WHERE custmr_nme = #{custmrNme}
AND custmr_mobl = #{custmrMobl}
AND custmr_id = #{custmrId};

mapper.update:
UPDATE custmr_tb SET
custmr_pw = #{custmrPw}
WHERE custmr_seq = #{custmrSeq};

mapper.insert:

		SELECT IFNULL(MAX(shipng_plc_seq),0)+1
		FROM shipping_place_tb;

    INSERT INTO shipping_place_tb(shipng_plc_seq
    					, shipng_plc_nme
    					, shipng_plc_postcod
    					, shipng_plc_adrs
    					, shipng_plc_detal_adrs
    					, recvr_nme
    					, recvr_mobl
    					, custmr_seq
    					, exist_check
    		 )
    VALUES(#{shipngPlcSeq}
    	,  #{shipngPlcNme}
    	,  #{shipngPlcPostcod}
    	,  #{shipngPlcAdrs}
    	,  #{shipngPlcDetalAdrs}
    	,  #{recvrNme}
    	,  #{recvrMobl}
    	,  #{custmrSeq}
    	,  #{existCheck}
    );

mapper.update:
UPDATE emp_tb SET
token = #{token}
WHERE emp_seq = #{empSeq};

UPDATE custmr_tb SET
custmr_token = #{token}
WHERE custmr_seq = #{custmrSeq};

    UPDATE shipping_place_tb SET
		exist_check = 'N'
	WHERE custmr_seq = #{custmrSeq};

    UPDATE shipping_place_tb SET
		shipng_plc_nme = #{shipngPlcNme}
	,   shipng_plc_postcod = #{shipngPlcPostcod}
	,   shipng_plc_adrs = #{shipngPlcAdrs}
	,   shipng_plc_detal_adrs = #{shipngPlcDetalAdrs}
	,   recvr_nme = #{recvrNme}
	,   recvr_mobl = #{recvrMobl}
	,   exist_check = #{existCheck}
	WHERE shipng_plc_seq = #{shipngPlcSeq}
	AND custmr_seq = #{custmrSeq};

mapper.delete:
    DELETE FROM shipping_place_tb
    WHERE shipng_plc_seq = #{shipngPlcSeq}
	AND custmr_seq = #{custmrSeq};

mapper.update:
UPDATE custmr_tb SET
login_dat = SYSDATE()
WHERE custmr_id = #{empId}
AND custmr_pw = #{empPw};
