mapper.select:
SELECT COUNT(*) FROM prodct_tb p JOIN catgr_tb c1 ON p.catgr_seq = c1.catgr_seq JOIN catgr_tb c2 ON c1.rot_catgr_seq = c2.catgr_seq JOIN catgr_tb c3 ON c2.rot_catgr_seq = c3.catgr_seq JOIN stck_tb s ON p.prodct_seq = s.prodct_seq WHERE p.prodct_seq NOT IN (SELECT prodct_seq FROM prodct_evnt_tb)
AND (c3.catgr_seq = (SELECT catgr_seq FROM catgr_tb WHERE catgr_seq = #{catgrSeq}) OR c2.catgr_seq = (SELECT catgr_seq FROM catgr_tb WHERE catgr_seq = #{catgrSeq}) OR c1.catgr_seq = (SELECT catgr_seq FROM catgr_tb WHERE catgr_seq = #{catgrSeq}))
AND p.prodct_nme LIKE '%${prodctSechText}%' AND qunt > 0 AND disply_check = 'Y' ORDER BY p.prodct_nme;

SELECT p.prodct_seq, main_img, prodct_nme, sel_pric, qunt FROM prodct_tb p JOIN catgr_tb c1 ON p.catgr_seq = c1.catgr_seq JOIN catgr_tb c2 ON c1.rot_catgr_seq = c2.catgr_seq JOIN catgr_tb c3 ON c2.rot_catgr_seq = c3.catgr_seq JOIN stck_tb s ON p.prodct_seq = s.prodct_seq WHERE p.prodct_seq not in (SELECT prodct_seq FROM prodct_evnt_tb)
AND (c3.catgr_seq = (SELECT catgr_seq FROM catgr_tb WHERE catgr_seq = #{catgrSeq}) OR c2.catgr_seq = (SELECT catgr_seq FROM catgr_tb WHERE catgr_seq = #{catgrSeq}) OR c1.catgr_seq = (SELECT catgr_seq FROM catgr_tb WHERE catgr_seq = #{catgrSeq}))
AND p.prodct_nme LIKE '%${prodctSechText}%' AND qunt > 0 AND disply_check = 'Y' ORDER BY p.prodct_nme LIMIT #{recordCountPerPage} OFFSET #{firstRecordIndex};

mapper.update:
UPDATE prodct_evnt_tb 
SET prodct_number = #{prodctNum} 	
WHERE 
	prodct_seq = #{prodctSeq};

mapper.insert:

		INSERT INTO ordr_recrd_tb
							(ordr_numbr_seq
							, ordr_amont
							, prodct_seq
					        , sel_pric
					        , instck_pric)
		VALUES((SELECT MAX(ordr_numbr_seq) FROM ordr_num_tb AS o)
					, #{prodctNum}
					, #{prodctSeq}
					, (SELECT IF(IFNULL(evnt_discnt_rat,0)=0, p.sel_pric, (p.sel_pric*(100-e.evnt_discnt_rat)/100)) FROM prodct_tb p LEFT JOIN evnt_bridg_tb e ON p.prodct_seq = e.prodct_seq WHERE p.prodct_seq = #{prodctSeq})
                    , (SELECT purchs_pric FROM prodct_tb WHERE prodct_seq = #{prodctSeq})
					);
