mapper.select:
SELECT custmr_seq , custmr_id , custmr_nme , custmr_gendr , custmr_mobl , custmr_card_check , custmr_quscnc_check , datediff(now(), login_dat) as dat , login_dat FROM custmr_tb WHERE custmr_quscnc_check = #{custmrQuscncCheck}
AND custmr_id LIKE '%${custmrSechText}%'
AND custmr_nme LIKE '%${custmrSechText}%'
AND custmr_mobl LIKE '%${custmrSechText}%'
AND custmr_card_check = '${custmrSechCardCheck}'
ORDER BY custmr_seq DESC LIMIT #{recordCountPerPage} OFFSET #{firstRecordIndex};

mapper.update:
		UPDATE	custmr_tb  
        SET		custmr_card_check = 'Y'         	
		WHERE 
		  custmr_seq in(
		  	 #{item} 
		  )

		UPDATE	custmr_tb  
        SET		custmr_mobl = #{custmrMobl}
        	 ,  custmr_postcd = #{custmrPostcd}
        	 ,  custmr_adrs = #{custmrAdrs}
        	 ,  custmr_detal_adrs = #{custmrDetalAdrs}
        	 ,  custmr_birth = #{custmrBirth}
        	 ,  custmr_eml = #{custmrEml}
			 ,  custmr_card_check = #{custmrCardCheck}
		WHERE 	custmr_seq = #{custmrSeq}

		UPDATE	custmr_tb  
        SET		custmr_quscnc_check = 'N'         	
		WHERE 
		  custmr_seq in(
		  	 #{item} 
		  )

mapper.delete:
DELETE	
FROM	custmr_tb      	
WHERE 
  custmr_seq in(
  	 #{item} 
  )
