mapper.select:
SELECT DATE_FORMAT(ordr_dat, '%Y-%m') as ordr_dat ,SUM(calOrdrCont) as calOrdrCont ,SUM(appOrdrCont) as appOrdrCont ,SUM(calSalsPric) as calSalsPric ,SUM(appSalsPric) as appSalsPric FROM ordr_anal_view
WHERE DATE_FORMAT(ordr_dat, '%Y-%m') BETWEEN DATE_FORMAT(now() - INTERVAL 2 month, '%Y-%m') and DATE_FORMAT(now(), '%Y-%m')
WHERE DATE_FORMAT(ordr_dat, '%Y-%m') BETWEEN DATE_FORMAT(now() - INTERVAL 5 month, '%Y-%m') and DATE_FORMAT(now(), '%Y-%m')
WHERE DATE_FORMAT(ordr_dat, '%Y-%m') BETWEEN DATE_FORMAT(now() - INTERVAL 11 month, '%Y-%m') and DATE_FORMAT(now(), '%Y-%m')
WHERE ordr_dat BETWEEN #{startDatSech} and #{endDatSech}
GROUP BY DATE_FORMAT(ordr_dat, '%Y-%m') ORDER BY ordr_dat ASC;

SELECT @ROWNUM := @ROWNUM + 1 AS rank ,A.prodct_seq ,A.prodct_nme ,A.sel_pric ,A.ordr_amont ,A.netinc ,A.sel_tot FROM(SELECT prodct_seq ,prodct_nme ,sel_pric ,ordr_amont ,sel_pric-instck_pric as 'netinc' ,sel_pric*ordr_amont as 'sel_tot' FROM total_anal_view
WHERE DATE_FORMAT(ordr_dat, '%Y-%m') BETWEEN DATE_FORMAT(now(), '%Y-%m') and DATE_FORMAT(now(), '%Y-%m')
WHERE DATE_FORMAT(ordr_dat, '%Y-%m') BETWEEN DATE_FORMAT(now() - INTERVAL 2 month, '%Y-%m') and DATE_FORMAT(now(), '%Y-%m')
WHERE DATE_FORMAT(ordr_dat, '%Y-%m') BETWEEN DATE_FORMAT(now() - INTERVAL 5 month, '%Y-%m') and DATE_FORMAT(now(), '%Y-%m')
WHERE DATE_FORMAT(ordr_dat, '%Y-%m') BETWEEN DATE_FORMAT(now() - INTERVAL 11 month, '%Y-%m') and DATE_FORMAT(now(), '%Y-%m')
WHERE ordr_dat BETWEEN #{startDatSech} and #{endDatSech}
AND top_catgr_nme = #{topCatgrNme}
AND mid_catgr_nme = #{midCatgrNme}
AND bot_catgr_nme = #{botCatgrNme}
AND sel_pric BETWEEN #{prodctpricSech1} and #{prodctpricSech2}
AND prodct_nme LIKE '%${prodctNmeSech}%' GROUP BY prodct_seq ORDER BY ordr_amont DESC)AS A, (SELECT @ROWNUM := 0) R;

SELECT @ROWNUM := @ROWNUM + 1 AS rank ,A.prodct_seq ,A.prodct_nme ,A.sel_pric ,A.ordr_amont ,A.netinc ,A.sel_tot FROM(SELECT prodct_seq ,prodct_nme ,sel_pric ,ordr_amont ,sel_pric-instck_pric as 'netinc' ,sel_pric*ordr_amont as 'sel_tot' FROM online_anal_view
WHERE DATE_FORMAT(ordr_dat, '%Y-%m') BETWEEN DATE_FORMAT(now(), '%Y-%m') and DATE_FORMAT(now(), '%Y-%m')
WHERE DATE_FORMAT(ordr_dat, '%Y-%m') BETWEEN DATE_FORMAT(now() - INTERVAL 2 month, '%Y-%m') and DATE_FORMAT(now(), '%Y-%m')
WHERE DATE_FORMAT(ordr_dat, '%Y-%m') BETWEEN DATE_FORMAT(now() - INTERVAL 5 month, '%Y-%m') and DATE_FORMAT(now(), '%Y-%m')
WHERE DATE_FORMAT(ordr_dat, '%Y-%m') BETWEEN DATE_FORMAT(now() - INTERVAL 11 month, '%Y-%m') and DATE_FORMAT(now(), '%Y-%m')
WHERE ordr_dat BETWEEN #{startDatSech} and #{endDatSech}
AND top_catgr_nme = #{topCatgrNme}
AND mid_catgr_nme = #{midCatgrNme}
AND bot_catgr_nme = #{botCatgrNme}
AND sel_pric BETWEEN #{prodctpricSech1} and #{prodctpricSech2}
AND prodct_nme LIKE '%${prodctNmeSech}%' GROUP BY prodct_seq ORDER BY ordr_amont DESC)AS A, (SELECT @ROWNUM := 0) R;

SELECT ordr_type
FROM ordr_type_tb
ORDER BY ORDR_TYPE_SEQ DESC;
