mapper.insert:
INSERT INTO
emp_tb(emp_seq
	,  id
	,  pw
	,  nme
	,  gendr
	,  postcd
	,  adrs
	,  detl_adrs
	,  birth
	,  hir_dat
	,  tel_hp
	,  mobl
	,  duty
	,  task
	,  img_rot)
VALUES((SELECT IFNULL(MAX(e.emp_seq),0) + 1 AS emp_seq FROM emp_tb as e)
	,  #{id}
	,  #{pw}
	,  #{nme}
	,  #{gendr}
	,  #{postcd}
	,  #{adrs}
	,  #{detlAdrs}
	,  #{birth}
	,  #{hirDat}
	,  #{tel}
	,  #{mobl}
	,  #{duty}
	,  #{task}
	,  #{imgRot});


	SELECT IFNULL(MAX(emp_seq),0) AS empSeq
	FROM emp_tb;

INSERT INTO
athrty_tb(athrty_seq	  
	,  emp_seq
	,  emp_athrty
	,  clint_athrty
	,  prodct_athrty
	,  cutmr_athrty
	,  today_athrty
	,  vactn_athrty
	,  exchng_flor_athrty
	,  evnt_athrty
	,  push_notfcatn_athrty
	,  prodct_anlys_athrty
	,  schedl_athrty
	,  stck_athrty
	,  together_athrty
	,  ordr_recrd_athrty
	,  ordr_anlys_athrty
	,  use_agremt_athrty
	,  sals_anlys_athrty
	,  djn_athrty)
VALUES((SELECT IFNULL(MAX(a.athrty_seq),0) + 1 AS athrty_seq FROM athrty_tb as a)
	,  #{empSeq}
	,  #{empAthrty}
	,  #{clintAthrty}
	,  #{prodctAthrty}
	,  #{cutmrAthrty}
	,  #{todayAthrty}
	,  #{vactnAthrty}
	,  #{exchngFlorAthrty}
	,  #{evntAthrty}
	,  #{pushNotfcatnAthrty}
	,  #{prodctAnlysAthrty}
	,  #{schedlAthrty}
	,  #{stckAthrty}
	,  #{togetherAthrty}
	,  #{ordrRecrdAthrty}
	,  #{ordrAnlysAthrty}
	,  #{useAgremtAthrty}
	,  #{salsAnlysAthrty}
	,  #{DJNAthrty});

mapper.update:
UPDATE emp_tb
SET id = #{id}
,   nme = #{nme}
,   postcd = #{postcd}
,   adrs = #{adrs}
,   detl_adrs = #{detlAdrs}
,   birth = #{birth}
,   hir_dat = #{hirDat}
,   tel_hp = #{tel}
,   mobl = #{mobl}
,   duty = #{duty}
,   task = #{task}
,   img_rot = #{imgRot}
WHERE emp_seq = #{empSeq};

UPDATE emp_tb
SET nme = #{nme}
,   postcd = #{postcd}
,   adrs = #{adrs}
,   detl_adrs = #{detlAdrs}
,   tel_hp = #{tel}
,   mobl = #{mobl}
,   img_rot = #{imgRot}
WHERE emp_seq = #{empSeq};

update.if:
