mapper.insert:

INSERT INTO schedl_tb
		   (schedl_seq
		  , schedl_titl
		  , schedl_start_dat
		  , schedl_end_dat
		  , schedl_col
		  , schedl_cnt
		  , schedl_rept				  
		  )				  				  
VALUES 	   						  						  	 
		  (  (SELECT IFNULL(max(s1.schedl_seq),0)+1
			  FROM   schedl_tb s1)
		  , #{title}			
		  , #{start}
		  , #{end}
		  , #{color}
		  , #{schedlCnt}
		  , #{schedlRept}				  				
		  )

mapper.update:
		UPDATE	schedl_tb  
        SET		schedl_titl = #{title}
        	 ,  schedl_start_dat = #{start}
        	 ,  schedl_end_dat = #{end}
        	 ,  schedl_col = #{color}
        	 ,  schedl_cnt = #{schedlCnt}




		WHERE 	schedl_seq = #{id}

mapper.select:
SELECT   if( day(#{schedlReptDat}) - DAY(#{start})>=0
	   , MONTH(#{schedlReptDat}) - MONTH(#{start})+1
	   , MONTH(#{schedlReptDat}) - MONTH(#{start})
	   )
FROM 	 DUAL;
