mapper.select:
SELECT p.prodct_seq , p.main_img , p.prodct_nme , p.sel_pric , f.SEL_STRT_TIME , f.stat FROM prodct_tb p JOIN fresh_fod_tb f ON p.prodct_seq = f.prodct_seq LEFT JOIN evnt_bridg_tb eb on p.prodct_seq = eb.prodct_seq LEFT JOIN sals_presnt_tb sp ON p.prodct_seq = sp.prodct_seq WHERE p.prodct_nme LIKE '%${freshSechText}%'
AND p.sel_pric <= ${freshMaxPric}
AND p.sel_pric >= ${freshMinPric}
AND p.sel_pric BETWEEN ${freshMinPric} AND ${freshMaxPric}
ORDER BY p.prodct_nme;

SELECT COUNT(*)
FROM prodct_tb p JOIN fresh_fod_tb f
ON p.prodct_seq = f.prodct_seq
LEFT JOIN evnt_bridg_tb eb
on p.prodct_seq = eb.prodct_seq
LEFT JOIN sals_presnt_tb sp
ON p.prodct_seq = sp.prodct_seq
WHERE p.prodct_nme LIKE '%${freshSechText}%'






	    AND p.sel_pric <= ${freshMaxPric}


	    AND p.sel_pric >= ${freshMinPric}


	    AND p.sel_pric BETWEEN ${freshMinPric} AND ${freshMaxPric}


ORDER BY
	p.prodct_nme;

mapper.update:
UPDATE fresh_fod_tb
SET stat = #{stat}
WHERE prodct_seq = #{prodctSeq};

mapper.select:
SELECT p.prodct_seq, main_img, prodct_nme, sel_pric, qunt FROM prodct_tb p JOIN catgr_tb c1 ON p.catgr_seq = c1.catgr_seq JOIN catgr_tb c2 ON c1.rot_catgr_seq = c2.catgr_seq JOIN catgr_tb c3 ON c2.rot_catgr_seq = c3.catgr_seq JOIN stck_tb s ON p.prodct_seq = s.prodct_seq WHERE p.prodct_seq not in (SELECT prodct_seq FROM fresh_fod_tb) AND p.prodct_type = 0
AND (c3.catgr_seq = (SELECT catgr_seq FROM catgr_tb WHERE catgr_seq = #{catgrSeq}) OR c2.catgr_seq = (SELECT catgr_seq FROM catgr_tb WHERE catgr_seq = #{catgrSeq}) OR c1.catgr_seq = (SELECT catgr_seq FROM catgr_tb WHERE catgr_seq = #{catgrSeq}))
AND p.prodct_nme LIKE '%${prodctSechText}%' AND qunt > 0 AND disply_check = 'Y' ORDER BY p.prodct_nme LIMIT #{recordCountPerPage} OFFSET #{firstRecordIndex};

SELECT COUNT(*) FROM prodct_tb p JOIN catgr_tb c1 ON p.catgr_seq = c1.catgr_seq JOIN catgr_tb c2 ON c1.rot_catgr_seq = c2.catgr_seq JOIN catgr_tb c3 ON c2.rot_catgr_seq = c3.catgr_seq JOIN stck_tb s ON p.prodct_seq = s.prodct_seq WHERE p.prodct_seq not in (SELECT prodct_seq FROM fresh_fod_tb) AND p.prodct_type = 0
AND (c3.catgr_seq = (SELECT catgr_seq FROM catgr_tb WHERE catgr_seq = #{catgrSeq}) OR c2.catgr_seq = (SELECT catgr_seq FROM catgr_tb WHERE catgr_seq = #{catgrSeq}) OR c1.catgr_seq = (SELECT catgr_seq FROM catgr_tb WHERE catgr_seq = #{catgrSeq}))
AND p.prodct_nme LIKE '%${prodctSechText}%' AND qunt > 0 AND disply_check = 'Y' ORDER BY p.prodct_nme;

mapper.update:
UPDATE prodct_tb
SET prodct_type = 3
WHERE prodct_seq = #{prodctSeq};

UPDATE prodct_tb
SET prodct_type = 0
WHERE prodct_seq = #{prodctSeq};
