mapper.select:
SELECT p.prodct_seq , p.main_img , p.prodct_nme , (h.amont - sp.sel_amont) 'remng_amont' , p.sel_pric , h.discnt_pric FROM prodct_tb p JOIN hotdl_tb h ON p.prodct_seq = h.prodct_seq LEFT JOIN evnt_bridg_tb eb on p.prodct_seq = eb.prodct_seq LEFT JOIN sals_presnt_tb sp ON p.prodct_seq = sp.prodct_seq WHERE p.prodct_nme LIKE '%${hotdlSechText}%'
AND h.discnt_pric <= ${hotdlMaxPric}
AND h.discnt_pric >= ${hotdlMinPric}
AND h.discnt_pric BETWEEN ${hotdlMinPric} AND ${hotdlMaxPric}
ORDER BY p.prodct_nme;

SELECT COUNT(*)
FROM prodct_tb p JOIN hotdl_tb h
ON p.prodct_seq = h.prodct_seq
LEFT JOIN evnt_bridg_tb eb
on p.prodct_seq = eb.prodct_seq
LEFT JOIN sals_presnt_tb sp
ON p.prodct_seq = sp.prodct_seq
WHERE p.prodct_nme LIKE '%${hotdlSechText}%'






	    AND h.discnt_pric <= ${hotdlMaxPric}


	    AND h.discnt_pric >= ${hotdlMinPric}


	    AND h.discnt_pric BETWEEN ${hotdlMinPric} AND ${hotdlMaxPric}


ORDER BY
	p.prodct_nme;

mapper.update:
UPDATE hotdl_tb
SET discnt_pric = ${discntPric}, amont = ${amont}
WHERE prodct_seq = ${prodctSeq};

mapper.select:
SELECT p.prodct_seq, main_img, prodct_nme, sel_pric, qunt FROM prodct_tb p JOIN catgr_tb c1 ON p.catgr_seq = c1.catgr_seq JOIN catgr_tb c2 ON c1.rot_catgr_seq = c2.catgr_seq JOIN catgr_tb c3 ON c2.rot_catgr_seq = c3.catgr_seq JOIN stck_tb s ON p.prodct_seq = s.prodct_seq WHERE p.prodct_seq not in (SELECT prodct_seq FROM hotdl_tb) AND p.prodct_type = 0
AND (c3.catgr_seq = (SELECT catgr_seq FROM catgr_tb WHERE catgr_seq = #{catgrSeq}) OR c2.catgr_seq = (SELECT catgr_seq FROM catgr_tb WHERE catgr_seq = #{catgrSeq}) OR c1.catgr_seq = (SELECT catgr_seq FROM catgr_tb WHERE catgr_seq = #{catgrSeq}))
AND p.prodct_nme LIKE '%${prodctSechText}%' AND qunt > 0 AND disply_check = 'Y' ORDER BY p.prodct_nme LIMIT #{recordCountPerPage} OFFSET #{firstRecordIndex};

SELECT COUNT(*) FROM prodct_tb p JOIN catgr_tb c1 ON p.catgr_seq = c1.catgr_seq JOIN catgr_tb c2 ON c1.rot_catgr_seq = c2.catgr_seq JOIN catgr_tb c3 ON c2.rot_catgr_seq = c3.catgr_seq JOIN stck_tb s ON p.prodct_seq = s.prodct_seq WHERE p.prodct_seq not in (SELECT prodct_seq FROM hotdl_tb) AND p.prodct_type = 0
AND (c3.catgr_seq = (SELECT catgr_seq FROM catgr_tb WHERE catgr_seq = #{catgrSeq}) OR c2.catgr_seq = (SELECT catgr_seq FROM catgr_tb WHERE catgr_seq = #{catgrSeq}) OR c1.catgr_seq = (SELECT catgr_seq FROM catgr_tb WHERE catgr_seq = #{catgrSeq}))
AND p.prodct_nme LIKE '%${prodctSechText}%' AND qunt > 0 AND disply_check = 'Y' ORDER BY p.prodct_nme;

mapper.update:
UPDATE prodct_tb
SET prodct_type = 4
WHERE prodct_seq = #{prodctSeq};

UPDATE prodct_tb
SET prodct_type = 0
WHERE prodct_seq = #{prodctSeq};
