mapper.select:
SELECT m.seqnc , p.purchs_pric , p.main_img , p.prodct_seq , p.prodct_nme , p.sel_pric , ifnull(round(p.sel_pric-(p.sel_pric*eb.evnt_discnt_rat)),'0') 'evnt_pric' , sp.SEL_AMONT FROM prodct_tb p JOIN main_display_tb m ON p.prodct_seq = m.prodct_seq LEFT JOIN evnt_bridg_tb eb ON p.prodct_seq = eb.prodct_seq LEFT JOIN sals_presnt_tb sp ON p.prodct_seq = sp.prodct_seq WHERE p.prodct_nme LIKE '%${mainDisplaySechText}%'
AND p.sel_pric <= ${mainDisplayMaxPric}
AND p.sel_pric >= ${mainDisplayMinPric}
AND p.sel_pric BETWEEN ${mainDisplayMinPric} AND ${mainDisplayMaxPric}
ORDER BY m.seqnc;

SELECT COUNT(*)
FROM prodct_tb p JOIN main_display_tb m
ON p.prodct_seq = m.prodct_seq
LEFT JOIN evnt_bridg_tb eb
ON p.prodct_seq = eb.prodct_seq
LEFT JOIN sals_presnt_tb sp
ON p.prodct_seq = sp.prodct_seq
WHERE p.prodct_nme LIKE '%${mainDisplaySechText}%'






	    AND p.sel_pric <= ${mainDisplayMaxPric}


	    AND p.sel_pric >= ${mainDisplayMinPric}


	    AND p.sel_pric BETWEEN ${mainDisplayMinPric} AND ${mainDisplayMaxPric}


ORDER BY
	m.seqnc;

SELECT p.prodct_seq, main_img, prodct_nme, sel_pric, qunt FROM prodct_tb p JOIN catgr_tb c1 ON p.catgr_seq = c1.catgr_seq JOIN catgr_tb c2 ON c1.rot_catgr_seq = c2.catgr_seq JOIN catgr_tb c3 ON c2.rot_catgr_seq = c3.catgr_seq JOIN stck_tb s ON p.prodct_seq = s.prodct_seq WHERE p.prodct_seq not in (SELECT prodct_seq FROM prodct_emp_tb) AND p.prodct_type = 0
AND (c3.catgr_seq = (SELECT catgr_seq FROM catgr_tb WHERE catgr_seq = #{catgrSeq}) OR c2.catgr_seq = (SELECT catgr_seq FROM catgr_tb WHERE catgr_seq = #{catgrSeq}) OR c1.catgr_seq = (SELECT catgr_seq FROM catgr_tb WHERE catgr_seq = #{catgrSeq}))
AND p.prodct_nme LIKE '%${prodctSechText}%' AND qunt > 0 AND disply_check = 'Y' ORDER BY p.prodct_nme LIMIT #{recordCountPerPage} OFFSET #{firstRecordIndex};

SELECT COUNT(*) FROM prodct_tb p JOIN catgr_tb c1 ON p.catgr_seq = c1.catgr_seq JOIN catgr_tb c2 ON c1.rot_catgr_seq = c2.catgr_seq JOIN catgr_tb c3 ON c2.rot_catgr_seq = c3.catgr_seq JOIN stck_tb s ON p.prodct_seq = s.prodct_seq WHERE p.prodct_seq not in (SELECT prodct_seq FROM prodct_emp_tb) AND p.prodct_type = 0
AND (c3.catgr_seq = (SELECT catgr_seq FROM catgr_tb WHERE catgr_seq = #{catgrSeq}) OR c2.catgr_seq = (SELECT catgr_seq FROM catgr_tb WHERE catgr_seq = #{catgrSeq}) OR c1.catgr_seq = (SELECT catgr_seq FROM catgr_tb WHERE catgr_seq = #{catgrSeq}))
AND p.prodct_nme LIKE '%${prodctSechText}%' AND qunt > 0 AND disply_check = 'Y' ORDER BY p.prodct_nme;

mapper.update:
		UPDATE main_display_tb
		SET seqnc = #{seqnc}
        WHERE prodct_seq = #{prodctSeq};


UPDATE main_display_tb
SET seqnc=@seqnc:=@seqnc+1
ORDER BY seqnc;

UPDATE prodct_tb
SET prodct_type = 2
WHERE prodct_seq = #{prodctSeq};

UPDATE prodct_tb
SET prodct_type = 0
WHERE prodct_seq = #{prodctSeq};
