mapper.select:
    SELECT LENGTH(prodct_seq)+1 as prodctSeqLength
	FROM prodct_tb
	WHERE prodct_seq = #{prodctSeq};

mapper.insert:

			SELECT IFNULL(MAX(grop_purchs_prodct_seq),0)+1 as grop_purchs_prodct_seq
			FROM grop_purchs_tb;

	    INSERT INTO 
	    grop_purchs_tb(grop_purchs_prodct_seq
	    			,  grop_purchs_seq
					,  ordr_star_dat
                    ,  ordr_end_dat
                    ,  min_ordr_amont
                    ,  max_ordr_amont
                    ,  exist_pric
                    ,  sel_pric
                    ,  titl
                    ,  stat
                    ,  prodct_nme
                    ,  main_img
                    ,  detl_img1
                    ,  detl_img2
                    ,  detl_img3)
		VALUES(#{gropPurchsProdctSeq}
			, lpad(#{prodctSeq}
         	,  #{prodctSeqLength},1)
			,  #{ordrStarDat}
			,  #{ordrEndDat}	
			,  #{minOrdrAmont}
			,  #{maxOrdrAmont}
			,  #{existPric}
			,  #{selPric}
			,  #{titl}
			,  #{stat}
			,  #{prodctNme}
			,  #{prodctMainImage}
			,  #{prodctDetlImageOne}
			,  #{prodctDetlImageTwo}
			,  #{prodctDetlImageThree}
			);

mapper.update:
    UPDATE grop_purchs_tb 
	SET ordr_star_dat = #{ordrStarDat}
	,   ordr_end_dat = #{ordrEndDat}
	,   min_ordr_amont = #{minOrdrAmont}
	,   max_ordr_amont = #{maxOrdrAmont}
	,   exist_pric = #{existPric}
	,   sel_pric = #{selPric}
	,   titl = #{titl}
	,   stat = #{stat}
	,   prodct_nme = #{prodctNme}
	WHERE grop_purchs_prodct_seq = #{gropPurchsProdctSeq};
